    // variabel global :
    double NoxAngka1, NoxAngka2, NoxHasil;
    int tanda;

    // Fungsi filter untuk validasi hanya boleh input angka
    private void FilterAngka(KeyEvent s) {
        if (Character.isAlphabetic(s.getKeyChar())) {
            s.consume();
            JOptionPane.showMessageDialog(null, "Maat, Anda salah input... \n"
                    + "Hanya menerima input Angka !!!");
        }
    }

