    private void noxButtonTambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_noxButtonTambahActionPerformed
        // code untuk tombol tambah +
        tanda = 1;
        NoxAngka1 = Double.parseDouble(NoxTextAngka1.getText());
        NoxAngka2 = Double.parseDouble(NoxTextAngka2.getText());
        NoxHasil = NoxAngka1 + NoxAngka2;
        NoxTextHasil.setText(Double.toString(NoxHasil));
    }//GEN-LAST:event_noxButtonTambahActionPerformed

    private void NoxTextAngka1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_NoxTextAngka1KeyTyped
        // Memasukkan fungsi Filter validasi angka pada event -> Key Typed
        FilterAngka(evt);
    }//GEN-LAST:event_NoxTextAngka1KeyTyped

    private void NoxTextAngka2KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_NoxTextAngka2KeyTyped
        // Memasukkan fungsi Filter validasi angka pada event -> Key Typed
        FilterAngka(evt);
    }//GEN-LAST:event_NoxTextAngka2KeyTyped

    private void noxButtonKurangActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_noxButtonKurangActionPerformed
        // code untuk tombol Kurang -
        tanda = 2;
        NoxAngka1 = Double.parseDouble(NoxTextAngka1.getText());
        NoxAngka2 = Double.parseDouble(NoxTextAngka2.getText());
        NoxHasil = NoxAngka1 - NoxAngka2;
        NoxTextHasil.setText(Double.toString(NoxHasil));
    }//GEN-LAST:event_noxButtonKurangActionPerformed

    private void noxButtonKaliActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_noxButtonKaliActionPerformed
        // code untuk tombol Kali X
        tanda = 3;
        NoxAngka1 = Double.parseDouble(NoxTextAngka1.getText());
        NoxAngka2 = Double.parseDouble(NoxTextAngka2.getText());
        NoxHasil = NoxAngka1 * NoxAngka2;
        NoxTextHasil.setText(Double.toString(NoxHasil));
    }//GEN-LAST:event_noxButtonKaliActionPerformed

